import React, { useEffect, useState } from "react"; // Import React hooks
import axios from "axios";
import { FiArrowLeftCircle } from "react-icons/fi";
import { useNavigate } from "react-router-dom";
import "./Orientation_Resources_Inventory.css";

function Orientation_Resources_Inventory() {
  const [items, setItems] = useState([]);

  const navigate = useNavigate();
  useEffect(() => {
    const fetchItems = async () => {
      try {
        const response = await axios.get(
          "http://localhost:3000/items/supplies",
          {
            headers: {
              Authorization: `Bearer ${localStorage.getItem("token")}`, // Assuming token is stored in localStorage
            },
          }
        );
        setItems(response.data);
      } catch (error) {
        console.error("Error fetching supplies:", error);
      }
    };

    fetchItems();
  }, []);

  const goToAddEdit = () => {
    navigate("/add-edit-orientation-resources");
  };

  const goToDashboard = () => {
    navigate("/dashboard");
  };

  return (
    <div className="uniform-inventory">
      <div className="back-icon-container">
        <FiArrowLeftCircle onClick={goToDashboard} className="back-icon" />
        <h1 className="title">ORIENTATION SUPPLIES</h1>
      </div>
      <div className="search-container">
        <input type="text" className="search-input" placeholder="Search..." />
        <button className="button" onClick={goToAddEdit}>
          ADD ITEM
        </button>
      </div>
      <div className="items-container">
        {items.length > 0 ? (
          items.map((item) => (
            <div key={item.id} className="item">
              {item.image && (
                <img src={item.image} alt={item.name} className="item-image" />
              )}
              <div className="item-details">
                <h2 className="item-title">{item.name}</h2>
                <p className="item-detail">
                  Location Annex: {item.location_annex}
                </p>
                <p className="item-detail">
                  Quantity Annex: {item.quantity_annex}
                </p>
                <p className="item-detail">Location HQ: {item.location_hq}</p>
                <p className="item-detail">Quantity HQ: {item.quantity_hq}</p>
              </div>
            </div>
          ))
        ) : (
          <p className="no-uniforms">No uniforms available.</p>
        )}
      </div>
    </div>
  );
}

export default Orientation_Resources_Inventory;
